//提供了，bookshelf所有API的定义，参考bookshelf下的其它API的使用。
    /**
     * @method browserAPI
     * @param {string} action Required
     * @param {service} service Optional
     */
    function browserAPI(action, service) {
        var fun;
        service = service || "bookshelf";
        if (service in browser && action in browser[service]) {
            fun = (function (service, action) {
                return function (succCallback, errCallback, options) {
                    try {
                        var browserMethod = browser[service][action];
                        if (typeof browserMethod == 'function') {
                            var callBack;
                            var name = action + Date.now();
                            var sucName = 'suc' + name;
                            var errName = 'err' + name;
                            W[sucName] = typeof succCallback == 'function' ? succCallback : '';
                            W[errName] = typeof errCallback == 'function' ? errCallback : '';
                            eval('callBack=function(o){try{o=JSON.parse(o);}catch(e){}' +
                                'if(typeof o == "object" && o && o.rtncode==="-1"){typeof ' + errName + ' == "function" && ' + errName + '(o);}' +
                                'else{typeof ' + sucName + ' == "function" && ' + sucName + '(o);}}');
                            browserMethod(callBack || null, errCallback || null, options || {});
                        } else {
                            return browserMethod;
                        }
                    } catch (e) {
                        typeof errCallback == 'function' && errCallback({
                            rtncode : -2,
                            error : e
                        });
                    }
                }
            })(service, action);
        }
        return fun;
    }
