//native 接口，获取用户身份
function mttGetUserInfo()
{
  try{
    browser.bookshelf.getLoginInfo(function(o){
      if( $.is.o(o) ){
        var u_guid = $.is.s(o.guid) ? o.guid : '';
        $.util.setCookie('Q-BOOKSHELF-GUID', u_guid);
        var u_sid  = $.is.s(o.sid)  ? o.sid  : '';
        $.util.setCookie('sid', u_sid);
      }
    }, function(e){
      console.log(JSON.stringify(e));
    })
  }
  catch(e){
    $.app.reportError('mttGetUserInfo err', e);
  }
}

