browser.define("browser.bookshelf.getLoginInfo", function(suc, err){
  var loginInfo = "";
  var info = {};
  if( typeof x5mtt !== "undefined" ){
    loginInfo = qb_bridge.exec(null, null, "account", "getLoginInfo", null)+"";
    if(loginInfo == "undefined" || loginInfo == "{}"){
      info = {};
    }
    else{
      info = JSON.parse(loginInfo);
    }
    suc && suc(info);
    return info;
  }
  else{
    info = {};
    err && err(info);
    return info;
  }
});

