// @param token        {string} 用户token
// @param offerid      {string} 支付idpf
// @param pf           {string} 平台信息,格式平台-渠道-系统-自定义
// @param pfkey        {string} 由平台下发，游戏传递给支付sdk,自研应用不校验
// @param iscoins      {int}    是否一级货币直接购买
// @param icondata     {string} 货币示意图 可以填空
// @param enablemodify {int}    是否可以修改充值金额
// @param zoneid       {string} 分区
// @param tokeurl      {string} 支付时，获取订单接口的url_params参数
// @param paychannal   {string} 支付渠道UnipayPlugAPI
function mttPrepay(token, offerid, pf, pfkey, iscoins, icondata, enablemodify, zoneid, tokeurl, paychannal, succ, err){
  var op = {};
  op.token = token;
  op.offerid = offerid;
  op.pf = pf;
  op.pfkey = pfkey;
  op.iscoins = iscoins;
  op.icondata = icondata;
  op.enablemodify = enablemodify;
  op.zoneid = zoneid;
  op.tokeurl = tokeurl;
  op.paychannal = paychannal;
  browser.bookshelf.Prepay(op, succ, err);
}

