function gethisdayStep()
                {
                    if(window.browser && window.browser.device && window.browser.device.getstephistory){
                        try{
                            browser.device.getstephistory(function(data){
                                                          if(data.errorcode < 0)
                                                          {
                                                          alert(data.msg);
                                                          }else
                                                          {
                                                          var out ="<table border=1> <TR><th>日期</th><th>步数</th><th>错误码</th></TR>";
                                                          for(var i = 0;i < data.steps.length; i ++)
                                                          {
                                                          var dateS = new Date();
                                                          dateS.setTime(data.steps[i].timestamp);
                                                          out +="<TR><td>"+dateS.toLocaleString()+"</td><td>"+data.steps[i].step+"</td><td>"+data.errorcode+"</td><tr>";
                                                          }
                                                          out +="</table>";
                                                          document.getElementById("idhis").innerHTML = out;
                                                          }
                                                          });

