function getAccountInfo() {
    var no_error = true,
    info = null;
    try {
        info = JSON.parse(browser.login.getAccountInfo(callbackSuccess, callbackFail, "123"));
        alert(JSON.stringify(info));
    } catch(e) {
        no_error = false;
        info = null;
    }

    if (no_error) {
        if (info.type == 1) {
            if (!info.hasOwnProperty("skey")) {
                try {
                    qqInfo = JSON.parse(browser.login.getLoginInfo(callbackSuccess, callbackFail));
                    alert(JSON.stringify(qqInfo));
                    info["skey"] = qqInfo.skey;
                    info["stweb"] = qqInfo.loginkey;
                    alert(JSON.stringify(info));
                } catch(e) {
                    info = null;
                }
            }
        }
    } else {
        try {
            qqInfo = JSON.parse(browser.login.getLoginInfo(callbackSuccess, callbackFail));
            info = {
                "qbid": "",
                "uin": qqInfo.uin,
                "token": qqInfo.sid,
                "nickname": qqInfo.nickname,
                "nickname": qqInfo.nickname,
                "head": qqInfo.head,
                "type": 1,
                "skey": qqInfo.skey,
                "stweb": qqInfo.loginkey,
            };
        } catch(e) {
            no_error = false;
            info = null;
        }
    }
    alert(JSON.stringify(info));
}

