  var op = {};
  op.offerId = offerId;
  op.openId = openId;
  op.openKey = openKey;
  op.sessionId = sessionId;
  op.sessionType = sessionType;
  op.pf = pf;
  op.pfKey = pfKey;
  op.payItem = payItem;
  op.productId = productId;
  op.isDepositGameCoin = isDepositGameCoin;
  op.productTyp = productType;
  op.zoneId = zoneId;
  op.varItem = varItem;
  browser.nowlive.recharge(op, dictExtra, callback);
}

