//终端5.1版本API，查询一个视频是否可以下载
com.Dal.x51_canDownload = function(videoId, srcs, succCallback) {
  try {
    var params = {
      host: [],
      videoId: videoId
    };

    for (var i = 0; i < srcs.length; i++) {
      params.host.push(CONF.sourceHost[srcs[i]]);
    }


    x5.canDownload(params, succCallback);
  } catch (ex) {
    //前端异常反馈显示有部分终端没有这个方法，需要跟进
    if(ex.message.indexOf("has no method \'canDownload\'") == -1) {
      $.jsRuntimeError(ex,'5.1检查视频可否下载异常');
    }
    return JSON.stringify({
      host:[]
    });
  }
};

com.Dal.x51_canDownload(videoId, srcs, function(data) {
  try {
    if ($.isNullOrEmpty(data) === true) {
      callBack(); 
      return;
    }
    console.log("Detail downloadInfo From EndPoint: " + data);
    var downloadInfos = JSON.parse(data);
    //5.1终端存在一个BUG，在没有可以下载的源的时候，将直接返回[]而不是期望的{host:[]}
    if (!downloadInfos || ! downloadInfos.host) {
      callBack(); 
      return;
    }
    var downloadInfoFromEndPoint = [];
    for (var i = 0; i < downloadInfos.host.length; i++) {
      downloadInfoFromEndPoint.push(CONF.sourceHost.indexOf(downloadInfos.host[i]));
    }
    console.log("Detail downloadInfo Merged: " + JSON.stringify(downloadInfoFromEndPoint));
    callBack(downloadInfoFromEndPoint);
  } catch(ex) {
    $.jsRuntimeError(ex, "终端解析是否可以下载回调异常");
    callBack();
    return;
  }
});

