var hasObject = function (objString, parent) {
    var strArray = objString.split(".");
    var parentObj = parent || window;

    for (var i = 0; i < strArray.length; i++) {
        if (!parentObj[strArray[i]]) {
            return false;
        } else {
            parentObj = parentObj[strArray[i]];
        }
    }

    return true;
};

var hasObjects = function (objStringArray) {
    for (var i = 0; i < objStringArray.length; i++) {
        if (hasObject(objStringArray[i]) !== true) {
            return false;
        }
    }

    return true;
};

var hasX5Object = function (x5String) {
    if (typeof qb_bridge === "undefined" && typeof x5 === "undefined") {
        return false;
    }

    //对于轻应用一律禁用X5特性
    if (/(lightapp|lapp)/ig.test(navigator.userAgent) === true) {
        return false;
    }

    //如果是微信
    if (/micromessenger/ig.test(ua) === true) {
        return false;
    } else if (/mobile.*qq/gi.test(ua) === true) {
        //如果是手Q
        return false;
    }

    //对于轻应用一律禁用X5特性
    if (typeof navigatorInfo !== "undefined" &&
        navigatorInfo &&
        navigatorInfo.x5_Qua &&
        /(lightapp|lapp)/ig.test(navigatorInfo.x5_Qua) === true) {
        return false;
    }

    //如果是非QB，则一律返回FALSE
    if (typeof navigatorInfo !== "undefined" && navigatorInfo && navigatorInfo.isQb === false) {
        return false;
    }

    return hasObject(x5String);
};

