  $fn.breakPoint();
  var validSniffHost = null;
  //前端异常反馈指出终端返回的数据可能存在问题
  try {
    validSniffHost = JSON.parse(result);
  } catch (ex) {
    return;
  }

  if (!validSniffHost || !validSniffHost.host) {
    return;
  }

  var invalidVideoSrc = _.filter(CONF.videoSrc, function (item) {
    var c = _.filter(validSniffHost.host, function (hItem) {
      return item.srcHost === hItem;
    });

    if (c && c.length > 0) {
      return false;
    } else {
      return true;
    }
  });

  if (invalidVideoSrc && invalidVideoSrc.length > 0) {
    _.each(invalidVideoSrc, function (item) {
      if (isLongVideo(iType) === true) {
        item.playEpisodeEnabled = false;
      } else {
        item.sPlayerEnabled = false;
      }
    });
  }
});

