var showLastPlayHistoryCore = function(lastHistory) { //{{{
  try {
    var historyName = dom.lastPlayHistory.querySelector(".name");
    if (!historyName) {
      $.hide(dom.lastPlayHistory);
      return;
    }
    if (lastHistory.totaltime > 0 && lastHistory.title.length > 0 && dom.lastPlayHistory.userClosed !== true) {
      if (lastHistory.playtime === - 1) {
        lastHistory.playtime = lastHistory.totaltime;
      }
      var percent = Math.floor(lastHistory.playtime / lastHistory.totaltime * 100);
      if (percent === 0) {
        percent = 1;
      }

      //如果播放时长为-1，则代表已播放完
      if(lastHistory.playtime === -1) {
        percent = 100;
      }

      historyName.innerHTML = lastHistory.title + "   " + String.format("观看至{0}%", percent);

      //获取缓存以判断是否存在更新显示
      var lastHistoryInCache = $.cache.get("lastPlayHistory") || null;
      if (lastHistoryInCache && lastHistoryInCache.title === lastHistory.title && lastHistoryInCache.percent === percent) {
        $.hide(dom.lastPlayhistory);
      } else {
        $.show(dom.lastPlayHistory);
        $.addClass(dom.lastPlayHistory, "show");
      }

      //更新缓存
      $.cache.set("lastPlayHistory", {
        percent: percent,
        title: lastHistory.title
      });
    } else {
      $.hide(dom.lastPlayHistory);
    }

    if (historyName.innerHTML.length === 0) {
      $.hide(dom.lastPlayHistory);
    }
  } catch(ex) {
    $.jsRuntimeError(ex, "显示上一次播放记录异常");
  }
} //}}}


//获取最近的三条播放记录
com.Dal.x51_getHistory(0, 3, function(jsonStr) {
  try {
    var data = null;
    if(!jsonStr) {
      data = {
        total: -1,
        history: []
      };
    } else {
      data = JSON.parse(jsonStr);
    }

    var playHistory = data;

    if (!$.isNullOrEmpty(playHistory) && playHistory.total > 0) {
      var lastHistory = playHistory.history[0];
      if (typeof lastHistory == "string") {
        lastHistory = JSON.parse(lastHistory);
      }
      showLastPlayHistoryCore(lastHistory);
    }
  } catch(ex) {
    $.jsRuntimeError(ex, "解析历史记录异常 " + jsonStr);
    return;
  }
});

